    static ArrayList<Khilafah> getListData() {
        ArrayList<Khilafah> listk = new ArrayList<>();
        for (int position = 0; position < khilafahName.length; position++) {
            Khilafah khilafah = new Khilafah();
            khilafah.setKhilafah(khilafahName[position]);
            khilafah.setDes(khilafahData[position]);
            khilafah.setPhoto(khilafahImg[position]);
            listk.add(khilafah);
        }
        return listk;
    }
